import React, { useState, useEffect } from "react";
import {
  Box,
  Heading,
  VStack,
  Avatar,
  Button,
  Input,
  FormControl,
  FormLabel,
  useToast,
  Text
} from "@chakra-ui/react";

export default function ProfilePage() {
  const [user, setUser] = useState(null);
  const [photo, setPhoto] = useState(null);
  const toast = useToast();

  // Charger l'utilisateur connecté depuis localStorage
  useEffect(() => {
    const users = JSON.parse(localStorage.getItem("users")) || [];
    const token = localStorage.getItem("token");

    if (!token || users.length === 0) {
      window.location.href = "/login"; // pas connecté
      return;
    }

    // Pour simplifier, on prend le premier utilisateur connecté
    const currentUser = users[users.length - 1];
    setUser(currentUser);
    setPhoto(currentUser.photo || null);
  }, []);

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const imgURL = URL.createObjectURL(file);
      setPhoto(imgURL);
      setUser((prev) => ({ ...prev, photo: imgURL }));
    }
  };

  const handleSave = () => {
    const users = JSON.parse(localStorage.getItem("users")) || [];

    // Mettre à jour l’utilisateur dans localStorage
    const updatedUsers = users.map((u) =>
      u.email === user.email ? { ...user, photo } : u
    );

    localStorage.setItem("users", JSON.stringify(updatedUsers));

    toast({
      title: "Profil mis à jour",
      status: "success",
      duration: 2000,
      isClosable: true,
    });
  };

  if (!user) return null;

  return (
    <Box
      minH="100vh"
      bg="principal.50"
      py={10}
      px={4}
      display="flex"
      justifyContent="center"
    >
      <VStack
        spacing={6}
        p={8}
        bg="white"
        boxShadow="xl"
        borderRadius="2xl"
        w="100%"
        maxW="500px"
      >
        <Heading as="h1" fontSize="h1" fontFamily="heading">
          Mon Profil
        </Heading>

        <Avatar size="xl" src={photo || "/logo.png"} />
        <Button
          as="label"
          htmlFor="photo-upload"
          bg="principal.500"
          color="white"
          _hover={{ bg: "principal.400" }}
        >
          Changer la photo
        </Button>
        <Input
          id="photo-upload"
          type="file"
          accept="image/*"
          onChange={handlePhotoChange}
          display="none"
        />

        {/* Nom modifiable */}
        <FormControl>
          <FormLabel>Nom complet</FormLabel>
          <Input
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />
        </FormControl>

        {/* Email modifiable */}
        <FormControl>
          <FormLabel>Email</FormLabel>
          <Input
            type="email"
            value={user.email}
            onChange={(e) => setUser({ ...user, email: e.target.value })}
          />
        </FormControl>

        {/* Téléphone modifiable */}
        <FormControl>
          <FormLabel>Téléphone</FormLabel>
          <Input
            value={user.phone || ""}
            onChange={(e) => setUser({ ...user, phone: e.target.value })}
          />
        </FormControl>

        {/* Genre non modifiable */}
        <FormControl>
          <FormLabel>Genre</FormLabel>
          <Text>{user.gender || "Non renseigné"}</Text>
        </FormControl>

        {/* Âge non modifiable */}
        <FormControl>
          <FormLabel>Âge</FormLabel>
          <Text>{user.age || "Non renseigné"}</Text>
        </FormControl>

        <Button
          color="white"
          bg="principal.500"
          _hover={{ bg: "principal.400" }}
          onClick={handleSave}
        >
          Sauvegarder
        </Button>
      </VStack>
    </Box>
  );
}
