import React, { useState } from "react";
import {
  Box,
  Heading,
  Input,
  Button,
  VStack,
  Text,
  useToast,
  Image
} from "@chakra-ui/react";

export default function RegisterPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const toast = useToast();

  const handleRegister = () => {
    const users = JSON.parse(localStorage.getItem("users")) || [];

    // Vérifie si l'email existe déjà
    if (users.find((u) => u.email === email)) {
      toast({
        title: "Erreur",
        description: "Cet email est déjà utilisé",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    // Sauvegarde utilisateur
    users.push({ email, password });
    localStorage.setItem("users", JSON.stringify(users));

    toast({
      title: "Inscription réussie",
      status: "success",
      duration: 2000,
      isClosable: true,
    });

    window.location.href = "/login";
  };

  return (
    <Box
      minH="100vh"
      bg="principal.50"
      display="flex"
      justifyContent="center"
      alignItems="center"
    >
      <VStack spacing={6} p={8} bg="white" boxShadow="xl" borderRadius="2xl">
        <Image src="/logo.png" alt="Logo" boxSize="60px" />
        <Heading as="h1" fontSize="h1" fontFamily="heading">
          Inscription
        </Heading>
        <Input
          placeholder="Email"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Input
          placeholder="Mot de passe"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button
          color="white"
          bg="principal.500"
          _hover={{ bg: "principal.400" }}
          onClick={handleRegister}
        >
          S'inscrire
        </Button>
        <Text fontSize="sm" color="gray.500">
          Déjà inscrit ? <a href="/login" style={{ color: "#1A75FF" }}>Connexion</a>
        </Text>
      </VStack>
    </Box>
  );
}
