import React, { useState } from "react";
import {
  Box,
  Heading,
  Input,
  Button,
  VStack,
  Text,
  useToast,
  Image,
  Link
} from "@chakra-ui/react";

export default function RegisterPage() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const toast = useToast();

  const handleRegister = async () => {
    try {
      const res = await fetch("/api/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, password }),
      });

      if (!res.ok) throw new Error("Impossible de créer le compte");

      toast({
        title: "Compte créé",
        description: "Vous pouvez maintenant vous connecter.",
        status: "success",
        duration: 2000,
        isClosable: true,
      });

      window.location.href = "/login";
    } catch (error) {
      toast({
        title: "Erreur",
        description: error.message,
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    }
  };

  return (
    <Box
      minH="100vh"
      bg="principal.50"
      display="flex"
      justifyContent="center"
      alignItems="center"
    >
      <VStack spacing={6} p={8} bg="white" boxShadow="xl" borderRadius="2xl">
        <Image src="/logo.png" alt="Logo" boxSize="60px" />
        <Heading as="h1" fontSize="h1" fontFamily="heading">
          Inscription
        </Heading>

        <Input
          placeholder="Nom complet"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <Input
          placeholder="Email"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Input
          placeholder="Mot de passe"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <Button
          color="white"
          bg="principal.500"
          _hover={{ bg: "principal.400" }}
          onClick={handleRegister}
        >
          S'inscrire
        </Button>

        <Text fontSize="sm" color="gray.500">
          Déjà un compte ?{" "}
          <Link color="principal.500" href="/login">
            Se connecter
          </Link>
        </Text>
      </VStack>
    </Box>
  );
}
