import React, { useState } from "react";
import {
  Box,
  Heading,
  Input,
  Button,
  VStack,
  Text,
  useToast,
  Image,
  FormControl,
  FormLabel,
  Select,
} from "@chakra-ui/react";

export default function RegisterPage() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [gender, setGender] = useState("");
  const [age, setAge] = useState("");
  const toast = useToast();

  const handleRegister = () => {
    const users = JSON.parse(localStorage.getItem("users")) || [];

    // Vérifie si email déjà utilisé
    if (users.find((u) => u.email === email)) {
      toast({
        title: "Erreur",
        description: "Cet email est déjà utilisé",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    // Vérifie si les mots de passe correspondent
    if (password !== confirmPassword) {
      toast({
        title: "Erreur",
        description: "Les mots de passe ne correspondent pas",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    // Vérifie si tous les champs sont remplis
    if (!name || !email || !password || !gender || !age) {
      toast({
        title: "Erreur",
        description: "Merci de remplir tous les champs",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    // Sauvegarde l'utilisateur
    users.push({ name, email, password, gender, age });
    localStorage.setItem("users", JSON.stringify(users));

    toast({
      title: "Inscription réussie",
      status: "success",
      duration: 2000,
      isClosable: true,
    });

    window.location.href = "/login";
  };

  return (
    <Box
      minH="100vh"
      bg="principal.50"
      display="flex"
      justifyContent="center"
      alignItems="center"
    >
      <VStack spacing={6} p={8} bg="white" boxShadow="xl" borderRadius="2xl" w="100%" maxW="400px">
        <Image src="/logo.png" alt="Logo" boxSize="60px" />
        <Heading as="h1" fontSize="h1" fontFamily="heading">
          Inscription
        </Heading>

        <FormControl>
          <FormLabel>Nom complet</FormLabel>
          <Input
            placeholder="Votre nom"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </FormControl>

        <FormControl>
          <FormLabel>Email</FormLabel>
          <Input
            placeholder="Votre email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </FormControl>

        <FormControl>
          <FormLabel>Mot de passe</FormLabel>
          <Input
            placeholder="Mot de passe"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </FormControl>

        <FormControl>
          <FormLabel>Confirmer le mot de passe</FormLabel>
          <Input
            placeholder="Confirmez le mot de passe"
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </FormControl>

        <FormControl>
          <FormLabel>Genre</FormLabel>
          <Select placeholder="Sélectionnez votre genre" value={gender} onChange={(e) => setGender(e.target.value)}>
            <option value="homme">Homme</option>
            <option value="femme">Femme</option>
            <option value="autre">Autre</option>
          </Select>
        </FormControl>

        <FormControl>
          <FormLabel>Âge</FormLabel>
          <Input
            placeholder="Votre âge"
            type="number"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
        </FormControl>

        <Button
          color="white"
          bg="principal.500"
          _hover={{ bg: "principal.400" }}
          onClick={handleRegister}
          w="100%"
        >
          S'inscrire
        </Button>

        <Text fontSize="sm" color="gray.500">
          Déjà inscrit ?{" "}
          <a href="/login" style={{ color: "#1A75FF" }}>
            Connexion
          </a>
        </Text>
      </VStack>
    </Box>
  );
}
