import React, { useState } from "react";
import {
  Box,
  Heading,
  Input,
  Button,
  VStack,
  Text,
  useToast,
  Image
} from "@chakra-ui/react";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const toast = useToast();

  const handleLogin = () => {
    const users = JSON.parse(localStorage.getItem("users")) || [];
    const user = users.find((u) => u.email === email && u.password === password);

    if (!user) {
      toast({
        title: "Erreur",
        description: "Identifiants invalides",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    // Simule un token de connexion
    localStorage.setItem("token", "fake-jwt-token");

    toast({
      title: "Connexion réussie",
      status: "success",
      duration: 2000,
      isClosable: true,
    });

    window.location.href = "/dashboard";
  };

  return (
    <Box
      minH="100vh"
      bg="principal.50"
      display="flex"
      justifyContent="center"
      alignItems="center"
    >
      <VStack spacing={6} p={8} bg="white" boxShadow="xl" borderRadius="2xl">
        <Image src="/logo.png" alt="Logo" boxSize="60px" />
        <Heading as="h1" fontSize="h1" fontFamily="heading">
          Connexion
        </Heading>
        <Input
          placeholder="Email"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Input
          placeholder="Mot de passe"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button
          color="white"
          bg="principal.500"
          _hover={{ bg: "principal.400" }}
          onClick={handleLogin}
        >
          Se connecter
        </Button>
        <Text fontSize="sm" color="gray.500">
          Pas encore de compte ? <a href="/register" style={{ color: "#1A75FF" }}>Inscription</a>
        </Text>
      </VStack>
    </Box>
  );
}
