import React from "react";
import { Box, Heading, VStack, Table, Thead, Tbody, Tr, Th, Td } from "@chakra-ui/react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

export default function HistoryPage() {
  // üìä Donn√©es simul√©es
  const data = {
    labels: ["Lun", "Mar", "Mer", "Jeu", "Ven", "Sam", "Dim"],
    datasets: [
      {
        label: "Temp√©rature (¬∞C)",
        data: [22, 24, 27, 29, 31, 28, 25],
        borderColor: "#1A75FF",
        backgroundColor: "rgba(26,117,255,0.2)",
      },
      {
        label: "Humidit√© (%)",
        data: [40, 42, 38, 35, 30, 45, 50],
        borderColor: "#FF4D29",
        backgroundColor: "rgba(255,77,41,0.2)",
      },
    ],
  };

  const alerts = [
    { id: 1, date: "2025-08-10", type: "Temp√©rature", valeur: "35¬∞C", intensite: "√âlev√©e" },
    { id: 2, date: "2025-08-12", type: "Humidit√©", valeur: "20%", intensite: "Critique" },
  ];

  return (
    <Box p={8}>
      <Heading mb={6}>Historique des mesures</Heading>
      <VStack spacing={8} align="stretch">
        <Box bg="white" p={6} borderRadius="lg" boxShadow="md">
          <Line data={data} />
        </Box>

        <Box bg="white" p={6} borderRadius="lg" boxShadow="md">
          <Heading size="md" mb={4}>Alertes r√©centes</Heading>
          <Table variant="simple">
            <Thead>
              <Tr>
                <Th>Date</Th>
                <Th>Type</Th>
                <Th>Valeur</Th>
                <Th>Intensit√©</Th>
              </Tr>
            </Thead>
            <Tbody>
              {alerts.map((a) => (
                <Tr key={a.id}>
                  <Td>{a.date}</Td>
                  <Td>{a.type}</Td>
                  <Td>{a.valeur}</Td>
                  <Td>{a.intensite}</Td>
                </Tr>
              ))}
            </Tbody>
          </Table>
        </Box>
      </VStack>
    </Box>
  );
}
