import React, { useState } from "react";
import {
  Box,
  Heading,
  VStack,
  Avatar,
  Button,
  Input,
  FormControl,
  FormLabel,
  Select,
  useToast,
  useColorModeValue,
  Text,
  HStack
} from "@chakra-ui/react";

export default function ProfilePage() {
  // Infos profil
  const [name, setName] = useState("John Doe");
  const [email, setEmail] = useState("john@example.com");
  const [phone, setPhone] = useState("0600000000");
  const [photo, setPhoto] = useState(null);

  // Pr√©f√©rences notifications
  const [type, setType] = useState("all");
  const [frequency, setFrequency] = useState("instant");
  const [channel, setChannel] = useState("email");

  const toast = useToast();

  // Simule des donn√©es m√©t√©o
  const temperature = 27;
  const humidity = 45;

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setPhoto(URL.createObjectURL(file));
    }
  };

  const handleSave = async () => {
    try {
      // Exemple appel API
      const res = await fetch("/api/profile", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, phone, type, frequency, channel }),
      });

      if (!res.ok) throw new Error("Erreur lors de la mise √† jour");

      toast({
        title: "Profil mis √† jour",
        status: "success",
        duration: 2000,
        isClosable: true,
      });
    } catch (error) {
      toast({
        title: "Erreur",
        description: error.message,
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    }
  };

  // üé® couleurs adaptatives
  const bgPage = useColorModeValue("principal.50", "gray.800");
  const bgCard = useColorModeValue("white", "gray.700");
  const textColor = useColorModeValue("gray.900", "white");
  const inputBg = useColorModeValue("gray.50", "gray.600");
  const labelColor = useColorModeValue("gray.700", "gray.200");

  return (
    <Box minH="100vh" bg={bgPage} py={10} px={4} display="flex" justifyContent="center">
      <VStack
        spacing={6}
        p={8}
        bg={bgCard}
        boxShadow="xl"
        borderRadius="2xl"
        w="100%"
        maxW="600px"
        color={textColor}
        align="stretch"
      >
        <Heading as="h1" fontSize="h1" fontFamily="heading" textAlign="center" color={textColor}>
          Mon Profil
        </Heading>

        {/* Photo de profil */}
        <VStack spacing={3}>
          <Avatar size="xl" src={photo || "/logo.svg"} />
          <Button
            as="label"
            htmlFor="photo-upload"
            bg="principal.500"
            color="white"
            _hover={{ bg: "principal.400" }}
          >
            Changer la photo
          </Button>
          <Input
            id="photo-upload"
            type="file"
            accept="image/*"
            onChange={handlePhotoChange}
            display="none"
          />
        </VStack>

        {/* Infos personnelles */}
        <FormControl>
          <FormLabel color={labelColor}>Nom complet</FormLabel>
          <Input value={name} onChange={(e) => setName(e.target.value)} bg={inputBg} />
        </FormControl>

        <FormControl>
          <FormLabel color={labelColor}>Email</FormLabel>
          <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} bg={inputBg} />
        </FormControl>

        <FormControl>
          <FormLabel color={labelColor}>T√©l√©phone</FormLabel>
          <Input value={phone} onChange={(e) => setPhone(e.target.value)} bg={inputBg} />
        </FormControl>

        {/* Pr√©f√©rences notifications */}
        <Heading size="md" mt={6} mb={2} color={textColor}>
          Pr√©f√©rences de notifications
        </Heading>

        <FormControl>
          <FormLabel color={labelColor}>Type de notifications</FormLabel>
          <Select value={type} onChange={(e) => setType(e.target.value)} bg={inputBg}>
            <option value="all">Toutes</option>
            <option value="alerts">Seulement alertes</option>
            <option value="none">Aucune</option>
          </Select>
        </FormControl>

        <FormControl>
          <FormLabel color={labelColor}>Fr√©quence</FormLabel>
          <Select value={frequency} onChange={(e) => setFrequency(e.target.value)} bg={inputBg}>
            <option value="instant">Instantan√©e</option>
            <option value="daily">Quotidienne</option>
            <option value="weekly">Hebdomadaire</option>
          </Select>
        </FormControl>

        <FormControl>
          <FormLabel color={labelColor}>Canal</FormLabel>
          <Select value={channel} onChange={(e) => setChannel(e.target.value)} bg={inputBg}>
            <option value="email">Email</option>
            <option value="sms">SMS</option>
            <option value="push">Notifications push</option>
          </Select>
        </FormControl>

        {/* Donn√©es m√©t√©o */}
        <Heading size="md" mt={6} mb={2} color={textColor}>
          Derni√®res donn√©es m√©t√©o
        </Heading>
        <HStack justify="space-between" bg={inputBg} p={4} borderRadius="md">
          <Text>üå°Ô∏è Temp√©rature : {temperature}¬∞C</Text>
          <Text>üíß Humidit√© : {humidity}%</Text>
        </HStack>

        {/* Bouton Sauvegarder */}
        <Button
          color="white"
          bg="principal.500"
          _hover={{ bg: "principal.400" }}
          onClick={handleSave}
          mt={4}
        >
          Sauvegarder
        </Button>
      </VStack>
    </Box>
  );
}
