import React, { useState } from "react";
import {
  Box,
  Heading,
  VStack,
  Avatar,
  Button,
  Input,
  FormControl,
  FormLabel,
  useToast
} from "@chakra-ui/react";

export default function ProfilePage() {
  const [name, setName] = useState("John Doe");
  const [email, setEmail] = useState("john@example.com");
  const [phone, setPhone] = useState("0600000000");
  const [photo, setPhoto] = useState(null);
  const toast = useToast();

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setPhoto(URL.createObjectURL(file));
    }
  };

  const handleSave = async () => {
    try {
      // Exemple appel API (ajuster selon backend)
      const res = await fetch("/api/profile", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, phone }),
      });

      if (!res.ok) throw new Error("Erreur lors de la mise à jour");

      toast({
        title: "Profil mis à jour",
        status: "success",
        duration: 2000,
        isClosable: true,
      });
    } catch (error) {
      toast({
        title: "Erreur",
        description: error.message,
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    }
  };

  return (
    <Box minH="100vh" bg="principal.50" py={10} px={4} display="flex" justifyContent="center">
      <VStack spacing={6} p={8} bg="white" boxShadow="xl" borderRadius="2xl" w="100%" maxW="500px">
        <Heading as="h1" fontSize="h1" fontFamily="heading">
          Mon Profil
        </Heading>

        <Avatar size="xl" src={photo || "/logo.png"} />
        <Button as="label" htmlFor="photo-upload" bg="principal.500" color="white" _hover={{ bg: "principal.400" }}>
          Changer la photo
        </Button>
        <Input id="photo-upload" type="file" accept="image/*" onChange={handlePhotoChange} display="none" />

        <FormControl>
          <FormLabel>Nom complet</FormLabel>
          <Input value={name} onChange={(e) => setName(e.target.value)} />
        </FormControl>

        <FormControl>
          <FormLabel>Email</FormLabel>
          <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </FormControl>

        <FormControl>
          <FormLabel>Téléphone</FormLabel>
          <Input value={phone} onChange={(e) => setPhone(e.target.value)} />
        </FormControl>

        <Button color="white" bg="principal.500" _hover={{ bg: "principal.400" }} onClick={handleSave}>
          Sauvegarder
        </Button>
      </VStack>
    </Box>
  );
}
